var link=document.querySelector(".write-us-button"),popup=document.querySelector(".modal-contact-us"),close=popup.querySelector(".modal-close"),name_surname=popup.querySelector("[name=name]"),email=popup.querySelector("[name=email]"),form=popup.querySelector("form");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),name_surname.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show"),popup.classList.remove("modal-error")}),form.addEventListener("submit",function(e){name_surname.value&&email.value||(e.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popup.classList.contains("modal-show")&&(popup.classList.remove("modal-show"),popup.classList.remove("modal-error")))});
